INSERT INTO `tripdetails` (`id`, `trips_id`, `name`, `pic`, `introduced1`, `introduced2`, `introduced3`, `introduced4`, `introduced5`, `created_at`, `updated_at`) VALUES
(1,	1,	'七美一日遊',	'https://i.imgur.com/CJ8gXjT.jpg',	'★ 特別的海灣造形!',	'★ 緩和的波浪及溫暖的南風!',	'★ 欣賞浪漫美麗的 雙 心 石 滬!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動適用於每年四至九月進行，冬季不建議進行。',	NULL,	NULL),
(2,	1,	'本島漫遊之旅',	'https://i.imgur.com/tDd8hkJ.jpg',	'★ 自然悠遊，恣意享受!',	'★ 飽覽澎湖海洋意象及澎湖灣風情!',	'★ 一趟體驗在地文化，自然與知性並重的文化之旅!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動全年適用。',	NULL,	NULL),
(3,	1,	'澎湖自然文化之旅',	'https://i.imgur.com/ReEOlN5.jpg	',	'★ 澎湖生活、文化、地方史及生態之旅!',	'★ 藍天白雲與紅花綠葉，和金色的沙蔚藍的海!',	'★ 一趟體驗在地文化，自然與知性並重的文化之旅!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動於每年四至九月進行，冬季不建議進行。',	NULL,	NULL),
(4,	2,	'玩遍本島。夜釣小管',	'https://i.imgur.com/8xQwjRb.jpg',	'★ 挑燈夜戰，清澈水色!',	'★ 新鮮小管沙西米　上　菜　囉!',	'★ 飽覽澎湖海洋意象及澎湖灣風情!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動適用於每年四至九月進行，冬季不建議進行。',	NULL,	NULL),
(5,	2,	'菊島北環&悠閒遊吉貝',	'https://i.imgur.com/9ooMPsq.jpg',	'★ 自然悠遊，恣意享受!',	'★ 飽覽澎湖海洋意象及澎湖灣風情!',	'★ 迷人的沙尾沙灘，碎貝殼與細沙在陽光的照耀閃著點點亮光!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動全年適用。',	NULL,	NULL),
(6,	2,	'望安 白沙 七美，離島二日遊',	'https://i.imgur.com/e2TZQCu.jpg',	'★ 浪漫的雙心石滬!',	'★ 藍天白雲與紅花綠葉，和金色的沙蔚藍的海!',	'★ 迷人的沙尾沙灘，碎貝殼與細沙在陽光的照耀閃著點點亮光!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動於每年四至九月進行，冬季不建議進行。',	NULL,	NULL),
(7,	3,	'古蹟&老街，澎湖文化與知性之旅',	'https://i.imgur.com/FrRMbi8.jpg',	'★ 走進時光隧道，現當年情景!',	'★ 飽覽珍貴的歷史文物及國家級古蹟!',	'★ 結合公共藝術、彩繪油桶、藝文展覽演出、水舞表演，讓人流連忘返!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動全年適用。',	NULL,	NULL),
(8,	3,	'東海生態之旅',	'https://i.imgur.com/3PDhmcB.jpg',	'★ 不能錯過的就是澎湖特種植物—澎湖仙人掌!',	'★ 海天一色的美景!',	'★ 純樸自然的濕地，孕育許多生物棲息!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動於每年四至九月進行，冬季不建議進行。',	NULL,	NULL),
(9,	3,	'玩遍澎湖。深度之旅',	'https://i.imgur.com/dTc8W7t.jpg',	'★ 巧奪天工的地質景觀!',	'★ 透亮清澈的海水!',	'★ 飽覽瑰麗多變的珊瑚!',	'★ 本專案不分平、假日均可使用。',	'★ 本活動適用於每年四至九月進行，冬季不建議進行。',	NULL,	NULL);
